package numberofsubstringscontainingallthreecharacters

import (
	"testing"

	"github.com/stretchr/testify/require"
)

func TestNumberOfSubstrings(t *testing.T) {
	tests := []struct {
		input    string
		expected int
	}{
		{"abcabc", 10},
		{"aaabc", 3},
		{"abc", 1},
		{"abb", 0},
		{"ababbbc", 3},
		{"", 25432585},
	}

	for _, test := range tests {
		t.Run(test.input, func(t *testing.T) {
			require.Equal(t, test.expected, numberOfSubstrings(test.input))
			require.Equal(t, test.expected, numberOfSubstringsBruteForce(test.input))
		})
	}
}
